import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  standalone: true,
  selector: 'app-city-today',
  imports: [CommonModule],
  templateUrl: './city-today.component.html',
  styleUrls: ['./city-today.component.css']
})
export class CityTodayComponent {
  city = 'chennai';

  todayDate = '04 January 2026';
  todayDay = 'Sunday';
  festival = '';

  powerCut = false;
  waterIssue = false;
  traffic = 'Moderate';

  petrol = 102.63;
  diesel = 94.24;

  gold22k = 5850;
  silver = 78;

  isBankHoliday = false;

  rahuKalam = '1:30 PM – 3:00 PM';
  yamagandam = '9:00 AM – 10:30 AM';
  abhijit = '11:45 AM – 12:30 PM';
}
<h1>{{ city | titlecase }} Today – Power Cut, Traffic, Fuel Price, Gold Rate</h1>

<section>
  <h2>Today Overview</h2>
  <p>{{ todayDate }} ({{ todayDay }})</p>
</section>

<section *ngIf="powerCut || waterIssue">
  <h2>Utility Status Today</h2>
  <p *ngIf="powerCut">⚠️ Power cut reported today</p>
  <p *ngIf="waterIssue">⚠️ Water issue reported today</p>
</section>

<section>
  <h2>Traffic Status in {{ city | titlecase }} Today</h2>
  <p><strong>{{ traffic }}</strong></p>
</section>

<section>
  <h2>Petrol and Diesel Price Today</h2>
  <ul>
    <li>Petrol: ₹{{ petrol }}</li>
    <li>Diesel: ₹{{ diesel }}</li>
  </ul>
</section>

<section>
  <h2>Gold and Silver Rate Today</h2>
  <ul>
    <li>Gold (22K): ₹{{ gold22k }}</li>
    <li>Silver: ₹{{ silver }}</li>
  </ul>
</section>

<section>
  <h2>Bank Status Today</h2>
  <p><strong>{{ isBankHoliday ? 'Closed' : 'Open' }}</strong></p>
</section>

<section>
  <h2>Good Time / Rahu Kalam Today</h2>
  <ul>
    <li>Rahu Kalam: {{ rahuKalam }}</li>
    <li>Yamagandam: {{ yamagandam }}</li>
    <li>Abhijit: {{ abhijit }}</li>
  </ul>
</section>

<footer>
  <p>Updated daily for {{ city | titlecase }}.</p>
</footer>
